mapper.select:
select
	t1.id                             ,
	t1.aric_id                        ,
	t1.aric_num                       ,
	t1.aric_model                     ,
	t1.custumer_id                    ,
	t1.flight_hour                    ,
	t1.flight_hour_dev                ,
	t1.takeoff_land                   ,
	t1.takeoff_land_dev               ,
	t1.date_type                      ,
	t1.flt_days                       ,
	t1.engine_work_time               ,
	t1.apu_work_time                  ,
	t1.start_time                     ,
	t1.end_time                       ,
	t1.aircratf_status                ,
	t1.grounded_reason                ,
	t1.cut_off_time                   ,
	t1.update_date                    ,
	t1.update_by                      ,
	t1.remark                         ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   
from csmp_om_aircraftdynamics 	t1


			and t1.ID =#{id}


			and t1.ARIC_ID =#{aricId}


			and t1.ARIC_NUM =#{aricNum}


			and t1.ARIC_MODEL =#{aricModel}


			and t1.CUSTUMER_ID =#{custumerId}


			and t1.FLIGHT_HOUR =#{flightHour}


			and t1.FLIGHT_HOUR_DEV =#{flightHourDev}


			and t1.TAKEOFF_LAND =#{takeoffLand}


			and t1.TAKEOFF_LAND_DEV =#{takeoffLandDev}


			and t1.DATE_TYPE =#{dateType}


			and t1.FLT_DAYS =#{fltDays}


			and t1.ENGINE_WORK_TIME =#{engineWorkTime}


			and t1.APU_WORK_TIME =#{apuWorkTime}


			and t1.START_TIME >= #{bean.startTimeBegin}


			and t1.START_TIME <= #{bean.startTimeEnd}


			and t1.END_TIME >= #{bean.endTimeBegin}


			and t1.END_TIME <= #{bean.endTimeEnd}


			and t1.AIRCRATF_STATUS =#{aircratfStatus}


			and t1.GROUNDED_REASON =#{groundedReason}


			and t1.CUT_OFF_TIME >= #{bean.cutOffTimeBegin}


			and t1.CUT_OFF_TIME <= #{bean.cutOffTimeEnd}


			and t1.UPDATE_DATE >= #{bean.updateDateBegin}


			and t1.UPDATE_DATE <= #{bean.updateDateEnd}


			and t1.UPDATE_BY =#{updateBy}


			and t1.REMARK =#{remark}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}
