select v.activityalias_ , v.businessstate_ , t1.id , t1.bulletin_no , t1.title , t1.organ_contact , t1.telephone , t1.bulletin_type , t1.material_supply , t1.implement_company , t1.report_date , t1.author , t1.file_company , t1.company_tel , t1.include_mars , t1.publish_date , t1.complete_date , t1.change_no , t1.perform_requirement , t1.change_comment , t1.remark , t1.report_no , t1.report_time , t1.reply_no , t1.reply_time , t1.complete_time , t1.forward_no , t1.forward_time , t1.created_by , t1.creation_date , t1.last_updated_by , t1.last_update_date , t1.last_update_ip , t1.version , t1.attribute_01 , t1.attribute_02 , t1.attribute_03 , t1.attribute_04 , t1.attribute_05 , t1.attribute_06 , t1.attribute_07 , t1.attribute_08 , t1.attribute_09 , t1.attribute_10 , t1.attribute_11 , t1.attribute_12 , t1.attribute_13 , t1.attribute_14 from hdkf_es_noticeinfo t1 left join bpm_entry_v v on t1.id = v.formid_
and t1.ID like '%'||#{bean.id}||'%'
and t1.BULLETIN_NO like '%'||#{bean.bulletinNo}||'%'
and t1.TITLE like '%'||#{bean.title}||'%'
and t1.ORGAN_CONTACT like '%'||#{bean.organContact}||'%'
and t1.TELEPHONE like '%'||#{bean.telephone}||'%'
and t1.BULLETIN_TYPE like '%'||#{bean.bulletinType}||'%'
and t1.MATERIAL_SUPPLY like '%'||#{bean.materialSupply}||'%'
and t1.IMPLEMENT_COMPANY like '%'||#{bean.implementCompany}||'%'
and t1.REPORT_DATE >= #{bean.reportDateBegin}
and t1.REPORT_DATE <= #{bean.reportDateEnd}
and t1.AUTHOR like '%'||#{bean.author}||'%'
and t1.FILE_COMPANY like '%'||#{bean.fileCompany}||'%'
and t1.COMPANY_TEL like '%'||#{bean.companyTel}||'%'
and t1.INCLUDE_MARS like '%'||#{bean.includeMars}||'%'
and t1.PUBLISH_DATE >= #{bean.publishDateBegin}
and t1.PUBLISH_DATE <= #{bean.publishDateEnd}
and t1.COMPLETE_DATE >= #{bean.completeDateBegin}
and t1.COMPLETE_DATE <= #{bean.completeDateEnd}
and t1.CHANGE_NO like '%'||#{bean.changeNo}||'%'
and t1.PERFORM_REQUIREMENT like '%'||#{bean.performRequirement}||'%'
and t1.CHANGE_COMMENT like '%'||#{bean.changeComment}||'%'
and t1.REMARK like '%'||#{bean.remark}||'%'
and t1.REPORT_NO like '%'||#{bean.reportNo}||'%'
and t1.REPORT_TIME >= #{bean.reportTimeBegin}
and t1.REPORT_TIME <= #{bean.reportTimeEnd}
and t1.REPLY_NO like '%'||#{bean.replyNo}||'%'
and t1.REPLY_TIME >= #{bean.replyTimeBegin}
and t1.REPLY_TIME <= #{bean.replyTimeEnd}
and t1.COMPLETE_TIME >= #{bean.completeTimeBegin}
and t1.COMPLETE_TIME <= #{bean.completeTimeEnd}
and t1.FORWARD_NO like '%'||#{bean.forwardNo}||'%'
and t1.FORWARD_TIME >= #{bean.forwardTimeBegin}
and t1.FORWARD_TIME <= #{bean.forwardTimeEnd}
and t1.CREATED_BY like '%'||#{bean.createdBy}||'%'
and t1.CREATION_DATE like '%'||#{bean.creationDate}||'%'
and t1.LAST_UPDATED_BY like '%'||#{bean.lastUpdatedBy}||'%'
and t1.LAST_UPDATE_DATE like '%'||#{bean.lastUpdateDate}||'%'
and t1.LAST_UPDATE_IP like '%'||#{bean.lastUpdateIp}||'%'
and t1.VERSION like '%'||#{bean.version}||'%'
and t1.ATTRIBUTE_01 like '%'||#{bean.attribute01}||'%'
and t1.ATTRIBUTE_02 like '%'||#{bean.attribute02}||'%'
and t1.ATTRIBUTE_03 like '%'||#{bean.attribute03}||'%'
and t1.ATTRIBUTE_04 like '%'||#{bean.attribute04}||'%'
and t1.ATTRIBUTE_05 like '%'||#{bean.attribute05}||'%'
and t1.ATTRIBUTE_06 like '%'||#{bean.attribute06}||'%'
and t1.ATTRIBUTE_07 like '%'||#{bean.attribute07}||'%'
and t1.ATTRIBUTE_08 like '%'||#{bean.attribute08}||'%'
and t1.ATTRIBUTE_09 like '%'||#{bean.attribute09}||'%'
and t1.ATTRIBUTE_10 like '%'||#{bean.attribute10}||'%'
and t1.ATTRIBUTE_11 like '%'||#{bean.attribute11}||'%'
and t1.ATTRIBUTE_12 like '%'||#{bean.attribute12}||'%'
and t1.attribute_13 >= #{bean.attribute13Begin}
and t1.attribute_13 <= #{bean.attribute13End}
and t1.ATTRIBUTE_14 like '%'||#{bean.attribute14}||'%'
and t1.CREATED_BY = #{bean.currUserId}
and v.businessstate_ = #{bean.bpmState}
and ${sfnConditions.sql}
order by t1.creation_date asc

