select
	t1.id                             ,
	t1.bulletin_no                    ,
	t1.title                          ,
	t1.organ_contact                  ,
	t1.telephone                      ,
	t1.bulletin_type                  ,
	t1.material_supply                ,
	t1.implement_company              ,
	t1.report_date                    ,
	t1.author                         ,
	t1.file_company                   ,
	t1.company_tel                    ,
	t1.include_mars                   ,
	t1.publish_date                   ,
	t1.complete_date                  ,
	t1.change_no                      ,
	t1.perform_requirement            ,
	t1.change_comment                 ,
	t1.remark                         ,
	t1.report_no                      ,
	t1.report_time                    ,
	t1.reply_no                       ,
	t1.reply_time                     ,
	t1.complete_time                  ,
	t1.forward_no                     ,
	t1.forward_time                   ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   
from hdkf_es_noticeinfo 	t1


			and t1.ID =#{id}


			and t1.BULLETIN_NO =#{bulletinNo}


			and t1.TITLE =#{title}


			and t1.ORGAN_CONTACT =#{organContact}


			and t1.TELEPHONE =#{telephone}


			and t1.BULLETIN_TYPE =#{bulletinType}


			and t1.MATERIAL_SUPPLY =#{materialSupply}


			and t1.IMPLEMENT_COMPANY =#{implementCompany}


			and t1.REPORT_DATE >= #{bean.reportDateBegin}


			and t1.REPORT_DATE <= #{bean.reportDateEnd}


			and t1.AUTHOR =#{author}


			and t1.FILE_COMPANY =#{fileCompany}


			and t1.COMPANY_TEL =#{companyTel}


			and t1.INCLUDE_MARS =#{includeMars}


			and t1.PUBLISH_DATE >= #{bean.publishDateBegin}


			and t1.PUBLISH_DATE <= #{bean.publishDateEnd}


			and t1.COMPLETE_DATE >= #{bean.completeDateBegin}


			and t1.COMPLETE_DATE <= #{bean.completeDateEnd}


			and t1.CHANGE_NO =#{changeNo}


			and t1.PERFORM_REQUIREMENT =#{performRequirement}


			and t1.CHANGE_COMMENT =#{changeComment}


			and t1.REMARK =#{remark}


			and t1.REPORT_NO =#{reportNo}


			and t1.REPORT_TIME >= #{bean.reportTimeBegin}


			and t1.REPORT_TIME <= #{bean.reportTimeEnd}


			and t1.REPLY_NO =#{replyNo}


			and t1.REPLY_TIME >= #{bean.replyTimeBegin}


			and t1.REPLY_TIME <= #{bean.replyTimeEnd}


			and t1.COMPLETE_TIME >= #{bean.completeTimeBegin}


			and t1.COMPLETE_TIME <= #{bean.completeTimeEnd}


			and t1.FORWARD_NO =#{forwardNo}


			and t1.FORWARD_TIME >= #{bean.forwardTimeBegin}


			and t1.FORWARD_TIME <= #{bean.forwardTimeEnd}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}

 select count(distinct batch_no) as countBatchNo
	  from  hdkf_ES_NOTICEINFO_EFFECTIVE eff 
	   where eff.bulletin_id = #{id}

select count(distinct customer_name) as countName
  from  hdkf_ES_NOTICEINFO_EFFECTIVE eff 
   where eff.bulletin_id = #{id}
