mapper.select:
select
	t1.id                             ,
	t1.task_id                        ,
	t1.task_no                        ,
	t1.borrow_name                    ,
	t1.borrow_dept                    ,
	t1.borrow_number                  ,
	t1.address                        ,
	t1.estimate_date                  ,
	t1.major                          ,
	t1.link_man_kf                    ,
	t1.link_phone_kf                  ,
	t1.task_demand                    ,
	t1.link_man_borrowed              ,
	t1.link_phone_borrowed            ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   
from hdkf_support_borrow_person_inf 	t1


			and t1.ID =#{id}


			and t1.TASK_ID =#{taskId}


			and t1.TASK_NO =#{taskNo}


			and t1.BORROW_NAME =#{borrowName}


			and t1.BORROW_DEPT =#{borrowDept}


			and t1.BORROW_NUMBER =#{borrowNumber}


			and t1.ADDRESS =#{address}


			and t1.ESTIMATE_DATE >= #{bean.estimateDateBegin}


			and t1.ESTIMATE_DATE <= #{bean.estimateDateEnd}


			and t1.MAJOR =#{major}


			and t1.LINK_MAN_KF =#{linkManKf}


			and t1.LINK_PHONE_KF =#{linkPhoneKf}


			and t1.TASK_DEMAND =#{taskDemand}


			and t1.LINK_MAN_BORROWED =#{linkManBorrowed}


			and t1.LINK_PHONE_BORROWED =#{linkPhoneBorrowed}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}
