mapper.select:
select
	t1.id                             ,
	t1.model_id                       ,
	t1.batch_no                       ,
	t1.sub_type                       ,
	t1.desige_by                      ,
	t1.product_by                     ,
	t1.contract_no                    ,
	t1.registrater_no                 ,
	t1.registrater_date               ,
	t1.finished_date                  ,
	t1.business_period_year           ,
	t1.business_period_hour           ,
	t1.is_business_period             ,
	t1.start_operate_date             ,
	t1.operate_status                 ,
	t1.engine1                        ,
	t1.engine2                        ,
	t1.number_of_flight               ,
	t1.flight_num                     ,
	t1.takeoff_landing_num            ,
	t1.custumer_name                  ,
	t1.operator                       ,
	t1.remark                         ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.company_id                     ,
	t1.total_flight_days                    ,
	t1.statistical_date                     ,
	t1.statistical_person                    
from csmp_bd_aircraftinfo 	t1


			and t1.ID =#{id}


			and t1.MODEL_ID =#{modelId}


			and t1.BATCH_NO =#{batchNo}


			and t1.SUB_TYPE =#{subType}


			and t1.DESIGE_BY =#{desigeBy}


			and t1.PRODUCT_BY =#{productBy}


			and t1.CONTRACT_NO =#{contractNo}


			and t1.REGISTRATER_NO =#{registraterNo}


			and t1.REGISTRATER_DATE >= #{bean.registraterDateBegin}


			and t1.REGISTRATER_DATE <= #{bean.registraterDateEnd}


			and t1.FINISHED_DATE >= #{bean.finishedDateBegin}


			and t1.FINISHED_DATE <= #{bean.finishedDateEnd}


			and t1.BUSINESS_PERIOD_YEAR =#{businessPeriodYear}


			and t1.BUSINESS_PERIOD_HOUR =#{businessPeriodHour}


			and t1.IS_BUSINESS_PERIOD =#{isBusinessPeriod}


			and t1.START_OPERATE_DATE >= #{bean.startOperateDateBegin}


			and t1.START_OPERATE_DATE <= #{bean.startOperateDateEnd}


			and t1.OPERATE_STATUS =#{operateStatus}


			and t1.ENGINE1 =#{engine1}


			and t1.ENGINE2 =#{engine2}


			and t1.NUMBER_OF_FLIGHT =#{numberOfFlight}


			and t1.FLIGHT_NUM =#{flightNum}


			and t1.TAKEOFF_LANDING_NUM =#{takeoffLandingNum}


			and t1.CUSTUMER_NAME =#{custumerName}


			and t1.OPERATOR =#{operator}


			and t1.REMARK =#{remark}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.COMPANY_ID =#{companyId}


			and t1.total_flight_days =#{totalFlightDays}


			and t1.statistical_date =#{statisticalDate}


			and t1.statistical_person =#{statisticalPerson}
