mapper.select:
select
t1.id ,

t1.dept_id ,
t1.company_code ,
t1.company_name ,
t1.name_en ,
t1.short_name ,
t1.short_name_en ,
t1.address ,
t1.receive_address ,
t1.company_type ,
t1.company_level ,
t1.country ,
t1.province ,
t1.setupdate ,
t1.business_nature ,
t1.operating_status ,
t1.language ,
t1.telephone ,
t1.fax ,
t1.mail ,
t1.account_name ,
t1.account_id ,
t1.account_address ,
t1.account_tel ,
t1.account_bank_name ,
t1.account_no ,
t1.describe ,
t1.contacts ,
t1.service_representative ,
t1.legal_person ,
t1.cl_client_code ,
t1.remark ,
t1.created_by ,
t1.creation_date ,
t1.last_updated_by ,
t1.last_update_date ,
t1.last_update_ip ,
t1.heat_sort ,
t1.version ,
t1.attribute_01 ,
t1.attribute_02 ,
t1.attribute_03 ,
t1.attribute_04 ,
t1.attribute_05 ,
t1.attribute_06 ,
t1.attribute_07 ,
t1.attribute_08 ,
t1.attribute_09 ,
t1.attribute_10
from csmp_bd_companyinfo t1


		and t1.ID =#{id}



		and t1.DEPT_ID =#{deptId}


		and t1.COMPANY_CODE =#{companyCode}


		and t1.COMPANY_NAME =#{companyName}


		and t1.NAME_EN =#{nameEn}


		and t1.SHORT_NAME =#{shortName}


		and t1.SHORT_NAME_EN =#{shortNameEn}


		and t1.ADDRESS =#{address}


		and t1.RECEIVE_ADDRESS =#{receiveAddress}


		and t1.COMPANY_TYPE =#{companyType}


		and t1.COMPANY_LEVEL =#{companyLevel}


		and t1.COUNTRY =#{country}


		and t1.PROVINCE =#{province}


		and t1.SETUPDATE >= #{bean.setupdateBegin}


		and t1.SETUPDATE <= #{bean.setupdateEnd}


		and t1.BUSINESS_NATURE =#{businessNature}


		and t1.OPERATING_STATUS =#{operatingStatus}


		and t1.LANGUAGE =#{language}


		and t1.TELEPHONE =#{telephone}


		and t1.FAX =#{fax}


		and t1.MAIL =#{mail}


		and t1.ACCOUNT_NAME =#{accountName}


		and t1.ACCOUNT_ID =#{accountId}


		and t1.ACCOUNT_ADDRESS =#{accountAddress}


		and t1.ACCOUNT_TEL =#{accountTel}


		and t1.ACCOUNT_BANK_NAME =#{accountBankName}


		and t1.ACCOUNT_NO =#{accountNo}


		and t1.DESCRIBE =#{describe}


		and t1.CONTACTS =#{contacts}


		and t1.SERVICE_REPRESENTATIVE =#{serviceRepresentative}


		and t1.LEGAL_PERSON =#{legalPerson}


		and t1.CL_CLIENT_CODE =#{clClientCode}


		and t1.REMARK =#{remark}


		and t1.CREATED_BY =#{createdBy}


		and t1.CREATION_DATE =#{creationDate}


		and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


		and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


		and t1.LAST_UPDATE_IP =#{lastUpdateIp}


		and t1.HEAT_SORT like '%'||#{bean.heatSort}||'%'


		and t1.VERSION =#{version}


		and t1.ATTRIBUTE_01 =#{attribute01}


		and t1.ATTRIBUTE_02 =#{attribute02}


		and t1.ATTRIBUTE_03 =#{attribute03}


		and t1.ATTRIBUTE_04 =#{attribute04}


		and t1.ATTRIBUTE_05 =#{attribute05}


		and t1.ATTRIBUTE_06 =#{attribute06}


		and t1.ATTRIBUTE_07 =#{attribute07}


		and t1.ATTRIBUTE_08 =#{attribute08}


		and t1.ATTRIBUTE_09 =#{attribute09}


		and t1.ATTRIBUTE_10 =#{attribute10}
