mapper.select:
select
	t1.id                             ,
	t1.request_id                     ,
	t1.request_no                     ,
	t1.cur_status                     ,
	t1.cur_bpmstate                   ,
	t1.cur_user                       ,
	t1.request_unit                   ,
	t1.batch_no                       ,
	t1.model_id                       ,
	t1.request_date                   ,
	t1.deal_id                        ,
	t1.deal_name                      ,
	t1.resp_id                        ,
	t1.resp_no                        ,
	t1.task_id                        ,
	t1.task_no                        ,
	t1.borrow_info_id                 ,
	t1.card_archive_id                ,
	t1.task_archive_id                ,
	t1.has_outfiedcard                ,
	t1.has_oapublishsheet             ,
	t1.has_fax                        ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   
from hdkf_support_process_view 	t1


			and t1.ID =#{id}


			and t1.REQUEST_ID =#{requestId}


			and t1.REQUEST_NO =#{requestNo}


			and t1.CUR_STATUS =#{curStatus}


			and t1.CUR_BPMSTATE =#{curBpmstate}


			and t1.CUR_USER =#{curUser}


			and t1.REQUEST_UNIT =#{requestUnit}


			and t1.BATCH_NO =#{batchNo}


			and t1.MODEL_ID =#{modelId}


			and t1.REQUEST_DATE >= #{bean.requestDateBegin}


			and t1.REQUEST_DATE <= #{bean.requestDateEnd}


			and t1.DEAL_ID =#{dealId}


			and t1.DEAL_NAME =#{dealName}


			and t1.RESP_ID =#{respId}


			and t1.RESP_NO =#{respNo}


			and t1.TASK_ID =#{taskId}


			and t1.TASK_NO =#{taskNo}


			and t1.BORROW_INFO_ID =#{borrowInfoId}


			and t1.CARD_ARCHIVE_ID =#{cardArchiveId}


			and t1.TASK_ARCHIVE_ID =#{taskArchiveId}


			and t1.HAS_OUTFIEDCARD =#{hasOutfiedcard}


			and t1.HAS_OAPUBLISHSHEET =#{hasOapublishsheet}


			and t1.HAS_FAX =#{hasFax}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}
